mapper.update:
/* [chn.bhmc.dms.ser.svi.service.dao.CampaignVinManageDAO.deleteUsedCamapaignInfo] */
UPDATE SE_1076T SET DEL_YN = 'Y'
 	, UPDT_USR_ID = #{regUseId}
	, UPDT_DT = SYSDATE
 WHERE DLR_CD =  #{dlrCd}
   AND VIN_NO = #{vinNo}
   AND CR_NO = #{crNo}
   AND LBR_CD = #{lbrCd}

mapper.select:
/* [chn.bhmc.dms.ser.svi.service.dao.CampaignVinManageDAO.selectCampaignUsedInfosCnt] */

   SELECT COUNT(A.CR_NO) AS VALUE
     FROM SE_1076T A , SE_0170T B
    WHERE A.DLR_CD    = B.DLR_CD
      AND A.CR_NO     = B.CR_NO
      AND A.LBR_CD    = B.LBR_CD
      AND A.DEL_YN    = 'N'
      AND B.PREFIX_ID = 'RO'
      AND B.DLR_CD    = #{sDlrCd}
      AND B.DOC_NO    = #{sRoDocNo}
      AND A.VIN_NO    = #{sVinNo}

/* [chn.bhmc.dms.ser.svi.service.dao.CampaignVinManageDAO.selectCampaignVinInfos] */
SELECT * FROM ( SELECT ROWNUM AS RNUM , T1.* FROM ( SELECT A.CR_NO ,A.VIN_NO ,'' LTS_MODEL_CD ,'' LTS_MODEL_NM ,A.USE_YN ,A.DLR_CD ,A.USE_DT FROM SE_1073T A WHERE CR_NO = #{sCrNo} UNION SELECT A.CR_NO , '' VIN_NO , A.LTS_MODEL_CD , (SELECT
LTS_EN_NM LTS_CHN_NM LTS_EN_NM
FROM SE_1064T WHERE LTS_MODEL_CD = A.LTS_MODEL_CD) AS LTS_MODEL_NM , '' AS USE_YN , '' AS DLR_CD , NULL AS USE_DT FROM SE_1074T A WHERE A.CR_NO = #{sCrNo} ORDER BY
CR_NO ASCDESC VIN_NO ASCDESC LTS_MODEL_CD ASCDESC
B.CR_NO ASC
CR_NO ASC
)T1
AND ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

/* [chn.bhmc.dms.ser.svi.service.dao.CampaignVinManageDAO.selectCampaignLbrInfosCnt] */

    SELECT COUNT(A.CR_NO) AS VALUE
       FROM SE_1070T A , SE_1072T B
      WHERE A.CR_NO = #{sCrNo}
        AND B.CR_NO = A.CR_NO

/* [chn.bhmc.dms.ser.svi.service.dao.CampaignVinManageDAO.selectCampaignLbrInfos] */
SELECT * FROM ( SELECT ROWNUM AS RNUM , T1.* FROM ( SELECT B.CR_NO ,A.PC_DSTIN_CD ,B.LBR_CD ,B.LBR_NM ,B.LBR_QTY ,B.CAU_ITEM_CD ,B.CAU_ITEM_NM ,B.CAU_CD ,B.CAU_NM ,B.PHEN_CD ,B.PHEN_NM ,B.LBR_HM ,B.LBR_AMT ,B.USE_YN FROM SE_1070T A , SE_1072T B WHERE A.CR_NO = #{sCrNo} AND B.CR_NO = A.CR_NO ORDER BY
B.CR_NO ASCDESC B.LBR_CD ASCDESC
B.CR_NO ASC
B.CR_NO ASC
) T1
AND ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

/* [chn.bhmc.dms.ser.svi.service.dao.CampaignVinManageDAO.selectCampaignItemsCnt] */

    SELECT COUNT(B.CR_NO) AS VALUE
     FROM SE_1071T A , SE_1072T B
    WHERE B.CR_NO   = A.CR_NO
      AND B.LBR_CD  = A.LBR_CD
      AND A.CR_NO   = #{sCrNo}

/* [chn.bhmc.dms.ser.svi.service.dao.CampaignVinManageDAO.selectCampaignItems] */
SELECT * FROM( SELECT ROWNUM AS RNUM , T1.* FROM ( SELECT B.CR_NO , B.LBR_CD , B.LTS_MODEL_CD, (SELECT
LTS_EN_NM LTS_CHN_NM
FROM SE_1064T WHERE LTS_MODEL_CD = B.LTS_MODEL_CD) AS LTS_MODEL_NM , B.ITEM_CD AS CAU_ITEM_CD , B.ITEM_NM AS CAU_ITEM_NM, B.ITEM_QTY FROM SE_1072T A , SE_1071T B WHERE B.CR_NO = A.CR_NO AND B.LBR_CD = A.LBR_CD AND A.CR_NO = #{sCrNo} ORDER BY
B.CR_NO ASCDESC B.LBR_CD ASCDESC B.ITEM_CD ASCDESC
B.CR_NO ASC
B.CR_NO ASC
)T1
AND ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

/* [chn.bhmc.dms.ser.svi.service.dao.CampaignVinManageDAO.selectCampaignLbrInfoPopup] */
SELECT ROWNUM AS RNUM ,B.CR_NO ,A.HOST_CD ,CASE WHEN A.HOST_CD ='01' THEN 'H' ELSE 'D' END DSTIN_CD ,A.MAK_NM AS CR_NM ,B.LBR_CD ,CASE WHEN SUBSTR(B.LBR_CD,1,2) = '99' THEN '04' ELSE '03' END AS LBR_TP ,B.CR_TP ,B.LBR_NM ,B.LBR_QTY ,B.CAU_ITEM_CD ,B.CAU_ITEM_NM ,B.CAU_CD ,B.CAU_NM ,B.PHEN_CD ,B.PHEN_NM ,B.LBR_HM ,B.LBR_PRC ,B.LBR_AMT ,(SELECT WART_RTE_RATE FROM SE_1065T WHERE DLR_CD = #{sDlrCd}
AND RPIR_TP = '03'
AND TO_DATE(TO_CHAR(SYSDATE,'YYYYMMDD'),'YYYYMMDD') BETWEEN APPLY_START_DT AND APPLY_END_DT AND BRAND_CD = #{sCarBrandCd} AND USE_YN = 'Y' ) AS LBR_RATE FROM CR_0701T A , SE_1072T B , ( SELECT CR_NO , LBR_CD FROM ( SELECT B.CR_NO , B.LBR_CD FROM CR_0701T A , SE_1072T B WHERE A.MAK_CD = B.CR_NO AND TO_DATE(TO_CHAR(SYSDATE,'YYYYMMDD'),'YYYYMMDD') BETWEEN A.START_DT AND A.END_DT AND B.USE_YN = 'Y' AND B.CR_NO = #{sCrNo} AND A.CR_END_TP = 'N' AND A.HOST_CD = #{sHostCd} AND A.MAK_TP_CD = '03' AND A.DEL_YN = 'N' AND A.DLR_CD = #{sDlrCd} MINUS SELECT B.CR_NO , B.LBR_CD FROM CR_0701T A, SE_1076T B WHERE A.MAK_CD = B.CR_NO AND TO_DATE(TO_CHAR(SYSDATE,'YYYYMMDD'),'YYYYMMDD') BETWEEN A.START_DT AND A.END_DT AND B.VIN_NO = #{sVinNo} AND B.CR_NO = #{sCrNo} AND A.CR_END_TP = 'N' AND A.HOST_CD = #{sHostCd} AND A.MAK_TP_CD = '03' AND A.DEL_YN = 'N' AND A.DLR_CD = #{sDlrCd} AND B.DEL_YN = 'N' ) ) C WHERE A.MAK_CD = B.CR_NO AND A.DLR_CD = B.DLR_CD AND A.DLR_CD = #{sDlrCd} AND A.MAK_CD = #{sCrNo} AND TO_DATE(TO_CHAR(SYSDATE,'YYYYMMDD'),'YYYYMMDD') BETWEEN A.START_DT AND A.END_DT AND B.USE_YN = 'Y' AND B.CR_NO = C.CR_NO AND B.LBR_CD = C.LBR_CD AND A.CR_END_TP = 'N' AND A.HOST_CD = #{sHostCd} AND A.MAK_TP_CD = '03' AND A.DEL_YN = 'N' AND NOT EXISTS ( SELECT CR_NO FROM SE_0300T R1 , SE_0170T R2 WHERE R1.DLR_CD = R2.DLR_CD AND R1.RO_DOC_NO = R2.DOC_NO AND R1.RO_TP = R2.RO_TP AND R2.PREFIX_ID = 'RO' AND R2.CR_NO IS NOT NULL AND R1.RO_STAT_CD != '06' AND R2.CR_NO = B.CR_NO --AND R2.LBR_CD = B.LBR_CD AND R1.VIN_NO = #{sVinNo} ) ORDER BY B.LBR_CD ASC

/* [chn.bhmc.dms.ser.svi.service.dao.CampaignVinManageDAO.selectCampaignDlrPartInfoPopup] */
SELECT * FROM( SELECT ROWNUM AS RNUM , T1.* FROM ( SELECT A.* ,B.GR_STRGE_CD ,B.GI_STRGE_CD FROM ( SELECT C1.MAK_CD AS CR_NO ,C1.HOST_CD ,C1.MAK_SUB_TP_CD AS CR_TP ,C1.MAK_NM AS CR_NM ,C2.DLR_CD ,C2.ITEM_CD ,C2.ITEM_NM ,NVL(C2.ITEM_QTY , 0) AS GI_QTY ,NVL(C2.ITEM_UNIT_CD,'EA') AS STOCK_UNIT_CD ,NVL(C2.ITEM_PRC,0) AS ITEM_PRC ,NVL(C2.ITEM_AMT,0) AS SALE_PRC_AMT ,NVL(C2.ITEM_TOT_AMT,0) AS ITEM_TOT_AMT ,NVL(C2.DS_AMT,0) AS DC_AMT ,NVL(C2.DS_RATE,0) AS DC_RATE FROM CR_0701T C1 , SE_1071T C2 WHERE C1.MAK_CD = C2.CR_NO AND C1.DLR_CD = C2.DLR_CD AND C2.CR_NO = #{sCrNo} AND C2.DLR_CD = #{sDlrCd} AND C1.CR_END_TP = 'N' AND C1.HOST_CD = #{sHostCd} AND C1.MAK_TP_CD = '03' --마케팅유형코드(CRM) AND C1.DEL_YN = 'N' AND NOT EXISTS ( SELECT CR_NO FROM SE_0300T R1 , SE_0160T R2 WHERE R1.DLR_CD = R2.DLR_CD AND R1.RO_DOC_NO = R2.DOC_NO AND R1.RO_TP = R2.RO_TP AND R2.PREFIX_ID = 'RO' AND R2.CR_NO IS NOT NULL AND R1.RO_STAT_CD != '06' AND R2.CR_NO = C2.CR_NO AND R2.ITEM_CD = C2.ITEM_CD AND R1.VIN_NO = #{sVinNo} ) ) A LEFT OUTER JOIN ( SELECT ITEM_CD ,ITEM_NM ,ITEM_EN_NM ,STOCK_UNIT_CD ,GR_STRGE_CD ,GI_STRGE_CD ,NVL( (SELECT MOVING_AVG_PRC FROM PT_0208T WHERE DLR_CD = A.DLR_CD AND STRGE_CD = A.GI_STRGE_CD AND ITEM_CD = A.ITEM_CD ) , NVL(A.MOVING_AVG_PRC,0)) AS MOVING_AVG_PRC ,NVL( (SELECT NVL(GRTE_PRC, 0) AS SALE_PRC_AMT FROM PT_0214T WHERE DLR_CD = A.DLR_CD AND ITEM_CD = A.ITEM_CD AND PRC_TP = '01' AND APPLY_YN = 'Y') ,0) AS SALE_PRC_AMT FROM PT_0201T A WHERE DLR_CD = #{sDlrCd} AND ITEM_DSTIN_CD = '02' AND END_YN = 'N' AND SPYR_CD = '01' AND BRAND_CD = DECODE(#{sCarBrandCd}, 'N', 'HD', 'OT') )B ON A.ITEM_CD = B.ITEM_CD ORDER BY
A.CR_NO ASCDESC A.ITEM_CD ASCDESC A.ITEM_NM ASCDESC A.GI_QTY ASCDESC
A.CR_NO ASC
A.CR_NO ASC
)T1
AND ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

/* [chn.bhmc.dms.ser.svi.service.dao.CampaignVinManageDAO.selectCampaignDlrPartInfoPopup] */
SELECT * FROM( SELECT ROWNUM AS RNUM , T1.* FROM ( SELECT A.* ,B.GR_STRGE_CD ,B.GI_STRGE_CD FROM ( SELECT C1.MAK_CD AS CR_NO ,C1.HOST_CD ,C1.MAK_SUB_TP_CD AS CR_TP ,C1.MAK_NM AS CR_NM ,C2.DLR_CD ,C2.ITEM_CD ,C2.ITEM_NM ,NVL(C2.ITEM_QTY , 0) AS GI_QTY ,NVL(C2.ITEM_UNIT_CD,'EA') AS STOCK_UNIT_CD ,NVL(C2.ITEM_PRC,0) AS ITEM_PRC ,NVL(C2.ITEM_AMT,0) AS SALE_PRC_AMT ,NVL(C2.ITEM_TOT_AMT,0) AS ITEM_TOT_AMT ,NVL(C2.DS_AMT,0) AS DC_AMT ,NVL(C2.DS_RATE,0) AS DC_RATE FROM CR_0701T C1 , SE_1071T C2 WHERE C1.MAK_CD = C2.CR_NO AND C1.DLR_CD = C2.DLR_CD AND C2.CR_NO = #{sCrNo} AND C2.DLR_CD = #{sDlrCd} AND C1.CR_END_TP = 'N' AND C1.HOST_CD = #{sHostCd} AND C1.MAK_TP_CD = '03' --마케팅유형코드(CRM) AND C1.DEL_YN = 'N' AND NOT EXISTS ( SELECT CR_NO FROM SE_0300T R1 , SE_0160T R2 WHERE R1.DLR_CD = R2.DLR_CD AND R1.RO_DOC_NO = R2.DOC_NO AND R1.RO_TP = R2.RO_TP AND R2.PREFIX_ID = 'RO' AND R2.CR_NO IS NOT NULL AND R1.RO_STAT_CD != '06' AND R2.CR_NO = C2.CR_NO AND R2.ITEM_CD = C2.ITEM_CD AND R1.VIN_NO = #{sVinNo} ) ) A LEFT OUTER JOIN ( SELECT ITEM_CD ,ITEM_NM ,ITEM_EN_NM ,STOCK_UNIT_CD ,GR_STRGE_CD ,GI_STRGE_CD ,NVL( (SELECT MOVING_AVG_PRC FROM PT_0208T WHERE DLR_CD = A.DLR_CD AND STRGE_CD = A.GI_STRGE_CD AND ITEM_CD = A.ITEM_CD ) , NVL(A.MOVING_AVG_PRC,0)) AS MOVING_AVG_PRC ,NVL( (SELECT NVL(GRTE_PRC, 0) AS SALE_PRC_AMT FROM PT_0214T WHERE DLR_CD = A.DLR_CD AND ITEM_CD = A.ITEM_CD AND PRC_TP = '01' AND APPLY_YN = 'Y') ,0) AS SALE_PRC_AMT FROM PT_0201T A WHERE DLR_CD = #{sDlrCd} AND ITEM_DSTIN_CD = '02' AND END_YN = 'N' AND SPYR_CD = '01' AND BRAND_CD = DECODE(#{sCarBrandCd}, 'N', 'HD', 'OT') )B ON A.ITEM_CD = B.ITEM_CD ORDER BY
A.CR_NO ASCDESC A.ITEM_CD ASCDESC A.ITEM_NM ASCDESC A.GI_QTY ASCDESC
A.CR_NO ASC
A.CR_NO ASC
)T1
AND ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

/* [chn.bhmc.dms.ser.svi.service.dao.CampaignVinManageDAO.selectFreeRoCnt] */
SELECT COUNT(CR_NO)
  FROM SE_1076T
 WHERE DLR_CD      = #{sDlrCd}
   AND VIN_NO      = #{sVinNo}
   AND RO_DOC_NO   = #{sRoDocNo}
   AND CR_TP       = 'K'        --면비보양

/* [chn.bhmc.dms.ser.svi.service.dao.CampaignVinManageDAO.selectFreeRoYnCnt] */
SELECT COUNT(CR_NO)
  FROM SE_1076T
 WHERE DLR_CD      = #{sDlrCd}
   AND VIN_NO      = #{sVinNo}
   AND RO_DOC_NO  != #{sRoDocNo}
   AND DEL_YN      = 'N'
   AND CR_TP       = 'K'        --면비보양
