  AND T1.DLR_CD = #{sDlrCd}
  AND T1.CUST_NO = #{sCustNo}

mapper.sql:
LEFT OUTER JOIN CM_0801T MNG_SC_NM ON T1.MNG_SC_ID = MNG_SC_NM.USR_ID
LEFT OUTER JOIN CM_0801T BEF_MNG_SC_NM ON T1.BEF_MNG_SC_ID = BEF_MNG_SC_NM.USR_ID
LEFT OUTER JOIN CM_0801T REG_USR_NM ON T1.REG_USR_ID = REG_USR_NM.USR_ID
LEFT OUTER JOIN CM_0801T UPDT_USR_NM ON T1.UPDT_USR_ID = UPDT_USR_NM.USR_ID

  AND T1.DLR_CD = #{sDlrCd}
  AND T1.SALE_OPPT_SEQ = #{sSaleOpptSeq}
   AND T1.SEQ            = #{sSeq}         
   AND T1.LEAD_STAT_CONT LIKE '%'||#{sLeadStatCont}||'%' 
   AND T1.SYS_MESG_CONT  LIKE '%'||#{sSysMesgCont}||'%'  
   AND T1.REG_DT         = #{sRegDt}       
   AND T1.UPDT_DT        = #{sUpdtDt}      
   AND T1.MNG_SC_ID      = #{sMngScId}     
   AND T1.REG_USR_ID     = #{sRegUsrId}    
   AND T1.UPDT_USR_ID    = #{sUpdtUsrId}

