/* [chn.bhmc.dms.crm.cso.service.dao.SalesOpptProcessMgmtDAO.selectSalesOpptProcessMgmtsHistLatestByCondition] */
SELECT T1.BEF_MNG_SC_ID, T1.MNG_SC_ID, T1.BEF_LEAD_STAT_CD , T1.LEAD_STAT_CD FROM (
    SELECT T1.*
    FROM CR_0211T T0, CR_0211HT T1
    WHERE T0.DLR_CD = #{sDlrCd}
    AND T0.CUST_NO = #{sCustNo}
    AND T0.SEQ = T1.SALE_OPPT_SEQ
    ORDER BY T1.REG_DT DESC
) T1
WHERE ROWNUM = 1

