/* [chn.bhmc.dms.crm.cso.service.dao.SalesOpptProcessMgmtDAO.selectLeadRepetitionByConditionCnt] */
SELECT COUNT(*) AS VALUE
FROM CR_0218T T1


    T1.DLR_CD = #{sDlrCd}

        AND T1.PURPOSE_TAG = #{sPurposeTag}


        AND T1.HOLD_TP = #{sHoldTp}


        AND T1.INFO_TP = #{sInfoTp}


        AND T1.INTR_CARLINE_CD = #{sIntrCarlineCd}



        AND ( T1.CUST_NM LIKE '%'||#{sCust}||'%'
              OR T1.HP_NO LIKE '%'||#{sCust}||'%'
            )



            AND    (T1.REG_DT >= trunc(#{sStartDt},'DD') AND T1.REG_DT < trunc(#{sEndDt},'DD')+1)


