/* [chn.bhmc.dms.par.ism.service.dao.IssueReqDAO.selectIssueReqByKey] */ SELECT A.DLR_CD ,A.PAR_REQ_DOC_NO ,A.PAR_GI_TP ,A.PAR_REQ_STAT_CD ,A.CANC_YN ,A.RO_DOC_NO ,A.RESV_DOC_NO ,A.PLT_CD ,A.SER_PRSN_ID ,A.CUST_CD ,A.CUST_NM ,A.VIN_NO ,A.CAR_NO ,A.READY_STAT_CD ,A.BORROW_STAT_CD ,A.BORROW_DOC_NO ,A.ETC_RV_STAT_CD ,A.ETC_RV_DOC_NO ,A.READY_DOC_NO ,A.RO_TP ,A.REMARK ,A.REG_USR_ID ,A.REG_DT ,A.UPDT_USR_ID ,CASE WHEN A.PAR_GI_TP = 'IN' THEN CASE WHEN A.PAR_REQ_STAT_CD = '02' THEN A.UPDT_DT ELSE NULL END ELSE A.UPDT_DT END UPDT_DT FROM PT_0501T A LEFT JOIN SE_0300T B ON A.DLR_CD = B.DLR_CD AND A.RO_DOC_NO = B.RO_DOC_NO AND A.RO_TP = B.RO_TP WHERE A.DLR_CD = #{dlrCd}
AND A.PAR_REQ_DOC_NO = #{parReqDocNo}
AND A.RO_DOC_NO = #{roDocNo}
AND A.PAR_GI_TP = #{parGiTp}

/* [chn.bhmc.dms.par.ism.service.dao.IssueReqDAO.selectIssueReqsByCondition] */ SELECT * FROM( SELECT ROWNUM AS RNUM ,T1.* FROM ( SELECT TT.* FROM ( SELECT A.DLR_CD ,A.PAR_REQ_DOC_NO ,A.PAR_GI_TP ,A.PAR_REQ_STAT_CD ,A.CANC_YN ,A.RO_DOC_NO ,A.RESV_DOC_NO ,C.ENGIN_NO ,A.PLT_CD ,CASE WHEN A.PAR_GI_TP = 'RO' THEN U2.USR_NM ELSE D.SA_NM END SER_PRSN_ID ,A.CUST_CD ,A.CUST_NM ,A.VIN_NO ,A.CAR_NO ,U.USR_NM AS REG_USR_ID ,A.REG_DT ,A.UPDT_USR_ID ,CASE WHEN A.PAR_GI_TP = 'IN' THEN CASE WHEN A.PAR_REQ_STAT_CD = '02' THEN A.UPDT_DT ELSE NULL END ELSE A.UPDT_DT END UPDT_DT ,A.READY_DOC_NO ,NVL(A.READY_STAT_CD, '01') AS READY_STAT_CD ,CASE WHEN NVL(A.BORROW_STAT_CD, '01') != '01' THEN A.BORROW_DOC_NO ELSE '' END AS BORROW_DOC_NO ,A.BORROW_DOC_NO AS BORROW_DOC_NO_DB ,NVL(A.BORROW_STAT_CD, '01') AS BORROW_STAT_CD ,A.ETC_RV_DOC_NO ,NVL(A.ETC_RV_STAT_CD, '01') AS ETC_RV_STAT_CD ,A.REMARK ,B.GI_DOC_NO ,B.GI_DOC_TP ,B.GI_DOC_STAT_CD ,B.MVT_DOC_YY_MM ,B.MVT_DOC_NO ,(SELECT MAX(AA.ETC_GI_TP) FROM PT_0502T AA WHERE AA.DLR_CD = A.DLR_CD AND AA.PAR_REQ_DOC_NO = A.PAR_REQ_DOC_NO ) AS ETC_GI_TP ,(SELECT COUNT(0) FROM PT_0502T AA WHERE AA.DLR_CD = A.DLR_CD AND AA.PAR_REQ_DOC_NO = A.PAR_REQ_DOC_NO ) AS ITEM_CNT ,NVL(D.RESV_STAT_CD, 'O') AS RESV_STAT_CD ,NVL(C.RO_STAT_CD, '01') AS RO_STAT_CD ,NVL(C.DL_STAT_CD, '01') AS DL_STAT_CD ,NVL(C.RO_TP, '01') AS RO_TP ,NVL(C.WRK_STAT_CD, '01') AS WRK_STAT_CD ,(SELECT COUNT(TT.RO_DOC_NO) FROM SE_0300T TT WHERE TT.DLR_CD = #{sDlrCd} AND TT.RESV_DOC_NO = A.RESV_DOC_NO) AS RO_DOC_NO_CNT ,E.PRE_AMT ,E.PAR_RESV_REMARK ,E.DRIVER_NM AS DRIVER_NM ,(SELECT CARLINE_CD AS CARLINE_CD FROM SA_0121T WHERE VIN_NO = A.VIN_NO AND ROWNUM = 1) AS CARLINE_CD ,(SELECT FN_GET_CARLINE_NM(CARLINE_CD) AS CARLINE_NM FROM SA_0121T WHERE VIN_NO = A.VIN_NO AND ROWNUM = 1) AS CARLINE_NM ,(SELECT FN_GET_MODEL_NM(MODEL_CD) AS MODEL_NM FROM SA_0121T WHERE VIN_NO = A.VIN_NO AND ROWNUM = 1) AS MODEL_NM ,(SELECT NVL(CAR_RUN_DIST_VAL,0) FROM SA_0121T WHERE VIN_NO = A.VIN_NO AND ROWNUM = 1) AS CAR_RUN_DIST_VAL ,C.CAR_OWNER_ID ,C.CAR_OWNER_NM ,C.DRIVER_ID ,C.DRIVER_NM ,C.RO_DT FROM PT_0501T A INNER JOIN CM_0801T U ON A.REG_USR_ID = U.USR_ID LEFT JOIN PT_0511T B ON A.DLR_CD = B.DLR_CD AND A.RO_DOC_NO = B.RO_DOC_NO LEFT JOIN SE_0300T C ON A.DLR_CD = C.DLR_CD AND A.RO_DOC_NO = C.RO_DOC_NO AND A.RO_TP = C.RO_TP LEFT JOIN CM_0801T U2 ON C.REG_USR_ID = U2.USR_ID LEFT JOIN SE_0100T D ON A.DLR_CD = D.DLR_CD AND A.RESV_DOC_NO = D.RESV_DOC_NO LEFT JOIN SE_0800T E ON A.DLR_CD = E.DLR_CD AND A.RESV_DOC_NO = E.PAR_RESV_DOC_NO LEFT JOIN( SELECT DLR_CD ,PAR_REQ_DOC_NO ,MAX(ETC_GI_TP) AS ETC_GI_TP FROM PT_0502T WHERE DLR_CD = #{sDlrCd} GROUP BY DLR_CD, PAR_REQ_DOC_NO ) F ON A.PAR_REQ_DOC_NO = F.PAR_REQ_DOC_NO LEFT JOIN( SELECT DLR_CD ,PAR_REQ_DOC_NO ,COUNT(PAR_REQ_DOC_NO) AS ITEM_CNT FROM PT_0502T WHERE DLR_CD = #{sDlrCd} GROUP BY DLR_CD, PAR_REQ_DOC_NO ) G ON A.PAR_REQ_DOC_NO = G.PAR_REQ_DOC_NO
AND A.DLR_CD = #{sDlrCd}
AND A.PAR_REQ_DOC_NO = #{sParReqDocNo}
AND A.PAR_REQ_DOC_NO LIKE '%'||#{sParReqDocNoLike}||'%'
AND A.PAR_GI_TP = #{sParGiTp}
AND A.PAR_REQ_STAT_CD = #{sParReqStatCd}
AND C.RO_TP = #{sRoTp}
AND C.RO_STAT_CD = #{sRoStatCd}
AND C.RO_STAT_CD IN ('01','02')
AND C.RO_STAT_CD NOT IN ('01','02')
AND C.RO_STAT_CD IS NOT NULL
AND A.PAR_REQ_DOC_NO IN ( SELECT PAR_REQ_DOC_NO FROM PT_0512T WHERE DLR_CD = #{sDlrCd} AND RECEIVE_ID LIKE '%'||#{sSaId}||'%' )
AND A.RO_DOC_NO IN ( SELECT RO_DOC_NO FROM SE_0330T WHERE DLR_CD = #{sDlrCd} AND WKGRP_NM LIKE '%'||#{sWkgrpNm}||'%' )
AND A.RO_DOC_NO IN ( SELECT RO_DOC_NO FROM SE_0330T WHERE DLR_CD = #{sDlrCd} AND REAL_TEC_NM LIKE '%'||#{sRealTecNm}||'%' )
AND A.CUST_CD LIKE '%'|| #{sCustCd} ||'%'
AND A.CUST_NM LIKE '%'|| #{sCustNm} ||'%'
AND A.VIN_NO LIKE '%'||#{sVinNo}||'%'
AND A.CAR_NO LIKE '%'||#{sCarNo}||'%'
AND B.GI_DOC_NO = #{sGiDocNo}
AND B.GI_DOC_TP = #{sGiDocTp}
AND B.GI_DOC_STAT_CD = #{sGiDocStatCd}
AND A.PAR_REQ_DOC_NO IN ( SELECT PAR_REQ_DOC_NO FROM PT_0512T WHERE DLR_CD = #{sDlrCd}
AND GI_DT >= #{sGiDocReqStartDt}
AND GI_DT <= #{sGiDocReqEndDt} + 1
)
AND A.PAR_REQ_DOC_NO IN ( SELECT PAR_REQ_DOC_NO FROM PT_0512T WHERE DLR_CD = #{sDlrCd}
