AND A.PAR_REQ_DOC_NO IN ( SELECT A.PAR_REQ_DOC_NO FROM PT_0512T A LEFT JOIN CM_0801T K3 ON A.RECEIVE_ID = K3.USR_ID WHERE A.DLR_CD = #{sDlrCd} AND K3.USR_NM LIKE '%'||#{sReceiveNm}||'%' )
AND A.PAR_REQ_DOC_NO IN ( SELECT A.PAR_REQ_DOC_NO FROM PT_0502T A WHERE A.DLR_CD = #{sDlrCd} AND A.ITEM_CD LIKE '%'||#{sItemCd}||'%' )
AND A.RO_DOC_NO LIKE '%'||#{sRoDocNo}||'%'
AND A.RO_DOC_NO = #{sRoDocNo}
