AND NVL(A.BORROW_STAT_CD, '01') = #{sBorrowStatCd}
AND A.BORROW_DOC_NO IS Not NULL AND A.REG_DT >= #{sBorrowStartDt}
AND A.BORROW_DOC_NO IS Not NULL AND A.REG_DT <= #{sBorrowEndDt} + 1
AND C.RO_DT >= #{sInvRoWorkStartDt}
AND C.RO_DT <= #{sInvRoWorkEndDt} + 1
AND A.PAR_REQ_DOC_NO IN ( SELECT A.PAR_REQ_DOC_NO FROM PT_0502T A LEFT JOIN CM_0801T K3 ON A.BORROW_USR_ID = K3.USR_ID LEFT JOIN CM_3401T K4 ON K3.DEPT_CD = K4.DEPT_CD WHERE A.DLR_CD = #{sDlrCd} AND A.BORROW_DOC_NO IS NOT NULL AND K4.DEPT_NM LIKE '%'||#{sBorrowDeptNm}||'%' )
AND A.PAR_REQ_DOC_NO IN ( SELECT A.PAR_REQ_DOC_NO FROM PT_0502T A LEFT JOIN CM_0801T K3 ON A.BORROW_USR_ID = K3.USR_ID LEFT JOIN CM_3401T K4 ON K3.DEPT_CD = K4.DEPT_CD WHERE A.DLR_CD = #{sDlrCd} AND A.BORROW_DOC_NO IS NOT NULL AND K3.USR_NM LIKE '%'||#{sBorrowUsrNm}||'%' )
