) T1
ROWNUM <= #{lastIndex}
ORDER BY
T1.RO_DT ASCDESC T1.PAR_REQ_DOC_NO ASCDESC T1.PAR_GI_TP ASCDESC T1.PAR_REQ_STAT_CD ASCDESC T1.RO_DOC_NO ASCDESC T1.RESV_DOC_NO ASCDESC T1.SER_PRSN_ID ASCDESC T1.CUST_CD ASCDESC T1.CUST_NM ASCDESC T1.VIN_NO ASCDESC T1.CAR_NO ASCDESC T1.CARLINE_CD ASCDESC T1.UPDT_DT ASCDESC T1.RO_TP ASCDESC T1.RO_STAT_CD ASCDESC T1.ENGIN_NO ASCDESC T1.CARLINE_NM ASCDESC ROWNUM ASCDESC T1.BORROW_DOC_NO ASCDESC T1.REG_DT ASCDESC T1.BORROW_STAT_CD ASCDESC
)
RNUM > #{firstIndex}

