/* [chn.bhmc.dms.par.ism.service.dao.IssueReqDAO.selectIssueReqStatuesByCondition] */ SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT A.DLR_CD ,A.PAR_REQ_DOC_NO ,A.PAR_REQ_DOC_LINE_NO ,A.PAR_REQ_STAT_CD ,G.PURC_REQ_NO ,A.CANC_YN ,A.PAR_GI_TP ,A.RO_DOC_NO ,A.RO_LINE_NO ,A.RESV_DOC_NO ,A.RESV_DOC_LINE_NO ,B.GR_STRGE_CD ,A.GI_STRGE_CD ,A.GI_LOC_CD ,A.ITEM_CD AS DB_ITEM_CD ,A.ITEM_CD ,I.PURC_PRC AS ITEM_PRC ,(I.PURC_PRC * A.REQ_QTY) AS ITEM_AMT ,(I.PURC_PRC * A.REQ_QTY) - (I.PURC_PRC * A.REQ_QTY * FN_GET_VAT(null,'ALL')) AS TAX_DDCT_AMT ,(I.PURC_PRC * A.REQ_QTY * FN_GET_VAT(null,'ALL')) AS TAX_AMT ,A.REQ_QTY ,B.STOCK_UNIT_CD AS UNIT_CD ,A.END_QTY ,A.REQ_STRGE_CD ,A.REG_DT ,A.REG_USR_ID ,A.UPDT_DT ,A.UPDT_USR_ID ,B.ITEM_NM ,#{sPltCd} AS PLT_CD ,NVL(C.AVLB_STOCK_QTY,0) AS AVLB_STOCK_QTY ,NVL(C.RESV_STOCK_QTY,0) AS RESV_STOCK_QTY ,NVL(C.CLAM_STOCK_QTY,0) AS CLAM_STOCK_QTY ,NVL(C.GR_SCHE_QTY,0) AS GR_SCHE_QTY ,NVL(C.GI_SCHE_QTY,0) AS GI_SCHE_QTY ,CASE WHEN A.END_QTY = 0 THEN A.REQ_QTY ELSE A.END_QTY END AS RESV_QTY ,A.REG_DT AS PURC_REQ_DT ,NVL(E.GI_DOC_NO,'-') AS GI_DOC_NO ,NVL(E.GI_DOC_TP,'01') AS GI_DOC_TP ,NVL(E.GI_DOC_STAT_CD,'01') AS GI_DOC_STAT_CD ,F.MVT_DOC_YY_MM ,F.MVT_DOC_NO ,F.MVT_DOC_LINE_NO ,H.CUST_CD ,H.CUST_NM ,H.SER_PRSN_ID ,H.VIN_NO ,H.CAR_NO ,H.VIN_NO AS CARLINE_NM ,B.ABC_IND ,B.CARLINE_CD ,'Y' AS DB_YN ,F.GI_STAT_CD ,F.RECEIVE_ID ,F.GI_DT ,F.RECEIVE_DT ,F.RETURN_ID ,F.RETURN_DT ,F.CANC_ID ,F.CANC_DT ,F.GI_CAU_NM ,A.ETC_GI_TP ,NVL(F.RETURN_PARTS_QTY, 0) AS RETURN_PARTS_QTY FROM PT_0502T A LEFT JOIN PT_0201T B ON A.DLR_CD = B.DLR_CD AND A.ITEM_CD = B.ITEM_CD LEFT JOIN PT_0301T C ON A.DLR_CD = C.DLR_CD AND A.ITEM_CD = C.ITEM_CD AND B.GR_STRGE_CD = C.STRGE_CD LEFT JOIN PT_0302T D ON A.DLR_CD = D.DLR_CD AND A.ITEM_CD = D.ITEM_CD AND B.GR_STRGE_CD = D.STRGE_CD AND A.RESV_DOC_NO = D.RESV_DOC_NO AND A.RESV_DOC_LINE_NO = D.RESV_DOC_LINE_NO LEFT JOIN PT_0511T E ON A.DLR_CD = E.DLR_CD AND A.PAR_REQ_DOC_NO = E.PAR_REQ_DOC_NO LEFT JOIN PT_0512T F ON A.DLR_CD = F.DLR_CD AND A.PAR_REQ_DOC_NO = F.PAR_REQ_DOC_NO AND A.RO_DOC_NO = F.RO_DOC_NO AND A.RO_LINE_NO = F.RO_DOC_LINE_NO LEFT JOIN PT_0411T G ON A.DLR_CD = G.DLR_CD AND A.RO_DOC_NO = G.REF_DOC_NO AND A.RO_LINE_NO = G.REF_DOC_LINE_NO AND A.ITEM_CD = G.ITEM_CD LEFT JOIN PT_0501T H ON A.DLR_CD = H.DLR_CD AND A.PAR_REQ_DOC_NO = H.PAR_REQ_DOC_NO LEFT OUTER JOIN ( SELECT DLR_CD ,ITEM_CD ,MAX(PRC_TP) AS PRC_TP ,MAX(LINE_NO) AS LINE_NO ,MAX(NVL(SALE_PRC_AMT,0)) AS PURC_PRC FROM PT_0214T WHERE DLR_CD = #{sDlrCd} AND DEL_YN != 'Y' GROUP BY DLR_CD, ITEM_CD ) I ON A.DLR_CD = I.DLR_CD AND A.ITEM_CD= I.ITEM_CD
AND A.DLR_CD = #{sDlrCd}
AND A.PAR_REQ_DOC_NO = #{sParReqDocNo}
AND A.RO_DOC_NO LIKE '%'||#{sRoDocNo}||'%'
AND A.PAR_GI_TP = #{sParGiTp}
AND A.PAR_REQ_STAT_CD = #{sParReqStatCd}
AND H.SER_PRSN_ID = #{sSerPrsnId}
AND H.CUST_CD = #{sCustCd}
AND H.CUST_NM LIKE '%'||#{sCustNm}||'%'
AND H.VIN_NO = #{sVinNo}
AND H.CAR_NO LIKE '%'||#{sCarNo}||'%'
AND A.REG_DT >= #{sReqStartDt}
AND A.REG_DT <= #{sReqEndDt} + 1
AND B.ABC_IND = #{sAbcInd}
AND B.ITEM_CD LIKE '%'|| #{sItemCd} ||'%'
AND B.ITEM_NM LIKE '%'|| #{sItemNm} ||'%'
AND B.CARLINE_CD = #{sCarLineCd}
ORDER BY
H.CUST_CD ASCDESC H.CUST_NM ASCDESC H.VIN_NO ASCDESC H.CAR_NO ASCDESC A.PAR_REQ_DOC_NO ASCDESC B.ITEM_CD ASCDESC B.ITEM_NM ASCDESC B.ABC_IND ASCDESC A.REQ_QTY ASCDESC A.UNIT_CD ASCDESC A.END_QTY ASCDESC A.PAR_REQ_STAT_CD ASCDESC A.PAR_GI_TP ASCDESC H.SER_PRSN_ID ASCDESC A.REG_DT ASCDESC
A.PAR_REQ_DOC_NO DESC, A.PAR_REQ_DOC_LINE_NO ASC
A.PAR_REQ_DOC_NO DESC, A.PAR_REQ_DOC_LINE_NO ASC
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

/* [chn.bhmc.dms.par.ism.service.dao.IssueReqDAO.selectIssueReqStatuesByConditionCnt] */
SELECT
    count(0)
FROM PT_0502T A
LEFT JOIN PT_0201T B ON A.DLR_CD = B.DLR_CD AND A.ITEM_CD = B.ITEM_CD
LEFT JOIN PT_0301T C ON A.DLR_CD = C.DLR_CD AND A.ITEM_CD = C.ITEM_CD AND B.GR_STRGE_CD = C.STRGE_CD
LEFT JOIN PT_0302T D ON A.DLR_CD = D.DLR_CD AND A.ITEM_CD = D.ITEM_CD AND B.GR_STRGE_CD = D.STRGE_CD AND A.RESV_DOC_NO = D.RESV_DOC_NO AND A.RESV_DOC_LINE_NO = D.RESV_DOC_LINE_NO
LEFT JOIN PT_0511T E ON A.DLR_CD = E.DLR_CD AND A.PAR_REQ_DOC_NO = E.PAR_REQ_DOC_NO
LEFT JOIN PT_0512T F ON A.DLR_CD = F.DLR_CD AND A.PAR_REQ_DOC_NO = F.PAR_REQ_DOC_NO AND A.RO_DOC_NO = F.RO_DOC_NO AND A.RO_LINE_NO = F.RO_DOC_LINE_NO
LEFT JOIN PT_0411T G ON A.DLR_CD = G.DLR_CD AND A.RO_DOC_NO = G.REF_DOC_NO AND A.RO_LINE_NO = G.REF_DOC_LINE_NO AND A.ITEM_CD = G.ITEM_CD
LEFT JOIN PT_0501T H ON A.DLR_CD = H.DLR_CD AND A.PAR_REQ_DOC_NO = H.PAR_REQ_DOC_NO


        AND A.DLR_CD = #{sDlrCd}



        AND A.PAR_REQ_DOC_NO = #{sParReqDocNo}



        AND A.RO_DOC_NO LIKE '%'||#{sRoDocNo}||'%'



        AND A.PAR_GI_TP = #{sParGiTp}



        AND A.PAR_REQ_STAT_CD = #{sParReqStatCd}



        AND H.SER_PRSN_ID = #{sSerPrsnId}



        AND H.CUST_CD = #{sCustCd}



        AND H.CUST_NM LIKE '%'||#{sCustNm}||'%'



        AND H.VIN_NO = #{sVinNo}



        AND H.CAR_NO LIKE '%'||#{sCarNo}||'%'



        AND A.REG_DT >= #{sReqStartDt}



        AND A.REG_DT <= #{sReqEndDt} + 1



        AND B.ABC_IND = #{sAbcInd}



        AND B.ITEM_CD LIKE '%'|| #{sItemCd} ||'%'



        AND B.ITEM_NM LIKE '%'|| #{sItemNm} ||'%'



       AND B.CARLINE_CD = #{sCarLineCd}
