mapper.insert:
/* [chn.bhmc.dms.par.pmm.service.dao.BinLocationMasterItemDAO.insertBinLocationMasterItem] */
INSERT INTO PT_0222T(
    DLR_CD
    ,LOC_ID
    ,PLT_CD
    ,STRGE_CD
    ,LVL_ID
    ,LOC_CD
    ,LOC_NM
    ,UPPER_LOC_ID
    ,ITEM_CD
    ,MAIN_LOC_YN
    ,UPPER_LVL_ID
    ,BASE_LVL_ID
    ,LOC_CD_MIG
    ,REG_USR_ID
    ,REG_DT
) VALUES (
    #{dlrCd}
    ,#{locId}
    ,#{pltCd}
    ,#{strgeCd}
    ,#{lvlId}
    ,#{locCd}
    ,#{locNm}
    ,#{upperLocId}
    ,#{itemCd}
    ,'N'
    ,#{lvlId}-1
    ,( select count(0)+1 from PT_0222T A WHERE A.DLR_CD = #{dlrCd} AND A.ITEM_CD IS NULL AND A.MAIN_LOC_YN = 'N' )
    ,#{locCdMig}
    ,#{regUsrId}
    ,SYSDATE
)

mapper.update:
/* [chn.bhmc.dms.par.pmm.service.dao.BinLocationMasterItemDAO.updateBinLocationMasterItem] */
UPDATE PT_0222T SET
    UPDT_DT = SYSDATE
    ,UPDT_USR_ID = #{updtUsrId}
    ,STRGE_CD = #{strgeCd}
    ,LOC_CD = #{locCd}
    ,LOC_NM = #{locNm}
    ,MAIN_LOC_YN = 'Y'
    ,LOC_CD_MIG = #{locCdMig}
    ,UPPER_LOC_ID = #{upperLocId}
WHERE DLR_CD = #{dlrCd}
AND LOC_ID = #{dbLocId}

/* [chn.bhmc.dms.par.pmm.service.dao.BinLocationMasterItemDAO.updateRealBinLocationMasterItem] */
UPDATE PT_0224T
   SET UPDT_DT = SYSDATE
      ,UPDT_USR_ID = #{updtUsrId}
      ,LOC_CD = #{locCd}
      ,LOC_NM = #{locNm}
      ,LOC_CD_MIG = #{locCdMig}
      ,REMARK = #{remark}
 WHERE DLR_CD   = #{dlrCd}
   AND STRGE_CD = #{strgeCd}
   AND ITEM_CD  = #{itemCd}

/* [chn.bhmc.dms.par.pmm.service.dao.BinLocationMasterItemDAO.deleteBinLocationMasterItem] */
DELETE FROM PT_0222T
WHERE DLR_CD = #{dlrCd}
AND LOC_CD = #{locCd}

/* [chn.bhmc.dms.par.pmm.service.dao.BinLocationMasterItemDAO.deleteRealBinLocationMasterItem] */
DELETE FROM PT_0224T
WHERE DLR_CD  = #{dlrCd}
 AND STRGE_CD = #{strgeCd}
 AND ITEM_CD  = #{itemCd}

mapper.select:
/* [chn.bhmc.dms.par.pmm.service.dao.BinLocationMasterDAO.selectBinLocationMasterItemsByCondition] */ SELECT * FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT B.STRGE_CD AS GR_STRGE_CD ,A.ITEM_CD ,A.ITEM_NM ,C.PLT_CD ,B.STRGE_CD AS STRGE_CD ,E.STRGE_TP ,C.LOC_CD AS DB_LOC_CD ,C.LOC_CD ,C.LOC_NM ,A.CARLINE_CD ,A.CARLINE_CD2 ,A.ITEM_DSTIN_CD ,C.LOC_CD_MIG FROM PT_0201T A INNER JOIN ( SELECT AA.DLR_CD, AA.ITEM_CD ,AA.STRGE_CD FROM PT_0301T AA WHERE 1= 1 AND AA.DLR_CD = #{sDlrCd} AND AA.STRGE_CD IS NOT NULL GROUP BY AA.DLR_CD ,AA.ITEM_CD ,AA.STRGE_CD )B ON A.DLR_CD = B.DLR_CD AND A.ITEM_CD = B.ITEM_CD --현재고 LEFT JOIN PT_0224T C ON B.DLR_CD = C.DLR_CD AND B.STRGE_CD = C.STRGE_CD AND B.ITEM_CD = C.ITEM_CD LEFT JOIN CM_0104T E ON B.STRGE_CD = E.STRGE_CD
AND A.DLR_CD = #{sDlrCd}
AND B.STRGE_CD = #{sStrgeCd}
AND B.STRGE_CD IN
#{item}
AND B.ITEM_CD LIKE '%'||#{sItemCd}||'%'
AND A.ITEM_NM LIKE '%'||#{sItemNm}||'%'
AND A.CARLINE_CD = #{sCarlineCd}
AND A.CARLINE_CD2 = #{sCarlineCd2}
AND A.ITEM_DSTIN_CD = #{sItemDstinCd}
AND C.LOC_CD is null
AND C.LOC_CD is not null
AND C.LOC_CD LIKE '%'||#{sLocCd}||'%'
AND C.lOC_CD_MIG LIKE '%'||#{slocCdMig}||'%' --lixinfei：仓库货位2 筛选条件
AND E.STRGE_TP = #{sStrgeTp}
ORDER BY
B.STRGE_CD ASCDESC E.STRGE_TP ASCDESC B.ITEM_CD ASCDESC A.ITEM_NM ASCDESC A.ITEM_DSTIN_CD ASCDESC A.CARLINE_CD ASCDESC A.CARLINE_CD2 ASCDESC C.LOC_CD ASCDESC C.LOC_NM ASCDESC
A.DLR_CD, B.STRGE_CD ASC, B.ITEM_CD ASC, C.LOC_CD ASC
A.DLR_CD ,B.STRGE_CD ASC, B.ITEM_CD ASC, C.LOC_CD ASC
) T1
ROWNUM <= #{lastIndex}
)
RNUM > #{firstIndex}

/* [chn.bhmc.dms.par.pmm.service.dao.BinLocationMasterDAO.selectBinLocationMasterItemsByConditionCnt] */
SELECT
  COUNT(0)
FROM PT_0201T A
INNER JOIN
(
    SELECT AA.DLR_CD, AA.ITEM_CD ,AA.STRGE_CD FROM PT_0301T AA WHERE 1= 1 AND AA.DLR_CD = #{sDlrCd} AND AA.STRGE_CD IS NOT NULL GROUP BY AA.DLR_CD ,AA.ITEM_CD ,AA.STRGE_CD
)B ON A.DLR_CD = B.DLR_CD AND A.ITEM_CD = B.ITEM_CD --현재고
LEFT JOIN PT_0224T C ON B.DLR_CD = C.DLR_CD AND B.STRGE_CD = C.STRGE_CD AND B.ITEM_CD = C.ITEM_CD
LEFT JOIN CM_0104T E ON B.STRGE_CD = E.STRGE_CD


        AND A.DLR_CD = #{sDlrCd}


        AND B.STRGE_CD = #{sStrgeCd}

     AND B.STRGE_CD IN

           #{item}



        AND B.ITEM_CD LIKE '%'||#{sItemCd}||'%'


        AND A.ITEM_NM LIKE '%'||#{sItemNm}||'%'


        AND A.CARLINE_CD = #{sCarlineCd}


        AND A.CARLINE_CD2 = #{sCarlineCd2}


        AND A.ITEM_DSTIN_CD = #{sItemDstinCd}




                 AND C.LOC_CD is null


                 AND C.LOC_CD is not null




        AND C.LOC_CD LIKE '%'||#{sLocCd}||'%'


        AND C.lOC_CD_MIG LIKE '%'||#{slocCdMig}||'%'   --lixinfei：仓库货位2  筛选条件


        AND E.STRGE_TP = #{sStrgeTp}

 /* [chn.bhmc.dms.par.pmm.service.dao.BinLocationMasterDAO.selectBinLocationMasterItemsByKeyCnt] */
 SELECT
   COUNT(0)
 FROM PT_0224T A
WHERE A.DLR_CD   = #{dlrCd}
  AND A.STRGE_CD = #{strgeCd}
  AND A.ITEM_CD  = #{itemCd}
